sqlMap.select:
SELECT userGroup, sum(credit) as sumCredit, count(userId) as memberCount FROM t_group_user WHERE groupId=#groupId#
and userGroup=#userGroup#
group by userGroup ORDER BY convert(userGroup,SIGNED) LIMIT #startNo#,#pageSize#;

SELECT r.groupKey as groupId, r.keyId as userGroup, r.teamName, r.creditCommissionRate, u.name as userName, u.headimgurl, u.userId FROM t_group_relation r, t_group_user gu, user_inf u WHERE r.groupKey = #groupId#
and r.keyId = #userGroup#
AND gu.groupId = #groupId# AND gu.userGroup = r.keyId AND gu.userRole = 10 AND u.userId = gu.userId ORDER BY r.teamGroup;
