sqlMap.select:
select * from user_inf where enterServer > 0
 isOnLine!=0 
and userId in ($userIds$);

select * from user_inf where 1=1
 playingTableId=0 
and userId in ($userIds$);

sqlMap.insert:
insert into
hb_exchange_record (userId, money, wxname, phone, createTime, state)
VALUES (#userId#, #money#, #wxname#, #phone#, #createTime#, #state#);
